import React, { Component } from 'react';
import moment from 'moment';
import Fetch from '../Fetch/Fetch';

import { Container, Row, Col } from 'react-bootstrap';
import './UserPanel.css';

const samplePlayer = {
    id: 1,
    first_name: "Yanis",
    last_name: "Thomas",
    company: "Renard, Breton and Lucas",
    city_name: "West Mattéo",
    last_seen: "2018-11-07T02:19:43.768Z",
    picture: "https://s3.amazonaws.com/uifaces/faces/twitter/operatino/128.jpg",
    total_events: 102,
    total_friends: 83
};

export default class UserPanel extends Component {
    constructor(props) {
        super(props);
        this.state = {


        }
    }

    render() {
        return (
            <Fetch path={'/players/1'}>

                {({ isFetching, hasFailed, data }) => {
                    if (hasFailed) return <h5>Servor error</h5>
                    if (isFetching) return <h5>Serveur is loading</h5>

                    if (data) {
                        const lastSeen = moment().diff(moment(new Date(data.last_seen)), 'months', true).toFixed();


                        return (
                            <div>

                                <Container className='userPosition'>
                                    <Row>
                                        <img className='player' src={this.state.picture} alt="player" />
                                    </Row>
                                    <Row>
                                        <h6>{this.state.player.first_name} {this.state.player.last_name} </h6>
                                    </Row>
                                    <Row>
                                        <ul>
                                            <li>Works at {this.state.player.company}</li>

                                            <li>Lives in {this.state.player.city_name}</li>
                                            <li>Last seen {lastSeen}months ago</li>
                                        </ul>
                                    </Row>
                                    <Row className='underlined'>

                                        <Col><h4 className='marginUnderlined'>{this.state.player.total_events}</h4><p className='smallEventsFriends'> events</p></Col>
                                        <Col><h4 className='marginUnderlined'>{this.state.player.total_friends}</h4><p className='smallEventsFriends'>friends</p></Col>

                                    </Row>
                                </Container>
                            </div>
                        )
                    }
                }}
            </Fetch>
        )
    }
}
