import React, { Component } from 'react';
import Fetch from '../Fetch/Fetch';
import './UpComingEventsTab.css';
import moment from 'moment';

export default class UpComingEventsTab extends Component {
  state = {
    data:[]
  }

  componentDidMount()  {
    Fetch("/players/1/lastEvents")
    .then(data => this.setState({data:data}))

}

  render() {
    const lastSeen = moment().diff(moment(new Date(this.state.data.last_seen)),'months', true).toFixed();
    return (
      <div>

          <h6 className="tabTitle">Upcoming Events</h6>
          {this.state.data.map((event,index)=> {let hour = moment(event.date).format('H:HH');
                  let dateFormated = moment(event.date).format('dddd D MMMM');
                  let diffMonth = moment(new Date(event.date)).diff(moment(), 'months', true).toFixed();

            return(
            <div className="eventsTab" key={index}>




              <p>in 3 months</p>
              <h3>{event.name}</h3>
              <p>Tuesday 29 January <br />at 20:09</p>

              <li>11 participants</li>
              </div>
              )
          } )}
            </div>



    );
  }
}
