import React from 'react';
import moment from 'moment';
import Fetch from '../Fetch/Fetch';

import { Container, Row, Col } from 'react-bootstrap';
import './UserPanel.css';



const UserPanel = ({ player}) =>{

        return (
            <Fetch path={`/players/${player}`}>

                {({ isFetching, hasFailed, data }) => {
                    if (hasFailed) return <h5>Servor error</h5>
                    if (isFetching) return <h5>Serveur is loading</h5>

                    if (data) {
                        const lastSeen = moment().diff(moment(new Date(data.last_seen)), 'months', true).toFixed();


                       return(
                            <div>

                                <Container className='userPosition'>
                                    <Row>
                                        <img className='player' src={data.first_name} alt="player1" />
                                    </Row>
                                    <Row>
                                        <h6>{data.first_name} {data.last_name} </h6>
                                    </Row>
                                    <Row>
                                        <ul>
                                            <li>Works at {data.company}</li>

                                            <li>Lives in {data.city_name}</li>
                                            <li>Last seen {lastSeen}months ago</li>
                                        </ul>
                                    </Row>
                                    <Row className='underlined'>

                                        <Col><h4 className='marginUnderlined'>{data.total_events}</h4><p className='smallEventsFriends'> events</p></Col>
                                        <Col><h4 className='marginUnderlined'>{data.total_friends}</h4><p className='smallEventsFriends'>friends</p></Col>

                                    </Row>
                                </Container>
                            </div>
                       )
                     }
                }}
            </Fetch>
         )
     };

     export default UserPanel;
